import React from 'react';
import ReactDOM from 'react-dom';
//import './demo0.js'
import './index.css';
import Confirm from './demo0.js';
import * as serviceWorker from './serviceWorker';


// 请写一个满足以下要求的confirm方法组件（可以参考一下 ， antd 的 Modal 组件的 Modal.info, Modal.waring . 这样的用法 ）：
//
// （1）能在任意组件(示例如下)的componentDidMount生命周期中挂载，并返回一个promise；
//
// （2）能通过返回值判断用户点击的是确定还是取消。
//
//
//
// async componentDidMount(){
//     let res = await confirm("确定删除吗")
//     if(res) {
//         console.log("是")
//     } else {
//         console.log("否")
//     }
// }

function confirm(props){
    function openModal(){
        this.setState({
            visible: true
        })
    }
    function onOk(){
        this.setState({
            visible:false
        })
    }
    function onCancel(){
        this.setState({
            visible:false
        })
    }
    return (
        <div className="App">
            <div onClick={openModal}>开启弹窗</div>
            <Confirm
                width={'520px'}
                height={'520px'}
                visible={props.visible}
                title={props.title}
                content={props.content}
                opacit={0.66}
                onOk={onOk}
                onCancel={onCancel}
            />

        </div>

    );
}

class App extends React.Component{
    constructor(props){
        super(props)
        this.state = {
            visible: true,
            title: 'React Modal',
            content: '欢迎使用！',
        }
    }



    render() {

        return (
            <div className="App">
            </div>

        );
    }


    async componentDidMount(){
        let res = await confirm(this.props)
        if(res) {
            console.log("是")
        } else {
            console.log("否")
        }
    }

}




ReactDOM.render(<App />, document.getElementById('root'));

// If you want your app to work offline and load faster, you can change
// unregister() to register() below. Note this comes with some pitfalls.
// Learn more about service workers: https://bit.ly/CRA-PWA
serviceWorker.unregister();
