/**
 * Created by weiyun on 2019/9/12.
 */
import React,{Component} from 'react';
import ReactDOM from 'react-dom';

var list = <div>list</div>
class List extends Component{
    render(){
        return (
            <div>
                list {this.props.name}
            </div>
        );
    }

    componentDidMount(){
        console.log('list did mount');
    }

    componentWillUpdate(){
        console.log('list will update');
    }
    componentWillUnmount(){
        console.log('list will unmount');
    }

}


class App extends Component{
    render(){
        return(
            <div>
                <List/>
                list
                <List/>
            </div>
        );
    }
}




class Child extends Component{
    render() {
        console.log('child render')
        return (
            <div>
                {this.props.name}
            </div>
        );
    }

    componentDidUpdate(prevProps, prevState) {
        console.log('child update')
    }

}

class Parent extends Component{
    state = {
        name: 'aaa'
    }
    render() {
        console.log('parent render')
        return (
            <div>
                <Child name={this.state.name}/>
                <button onClick={e => {

                    this.setState({name:'bbb'})
                }}>change</button>
            </div>
        );
    }
    componentDidUpdate(prevProps, prevState) {
        console.log('parent update')
    }
}


class ChildA extends Component{
    render() {
        return (
            <div>
                {this.props.name}
            </div>
        );
    }
    componentDidMount() {
        console.log('mount',this.props.name)
    }
    componentDidUpdate() {
        console.log('update',this.props.name)
    }
    componentWillUnmount() {
        console.log('unmount',this.props.name)
    }

}
class AppA extends Component{
    state = {
        list: ['a','b','c']
    }
    render() {
        return (
            <div>
                {this.state.list.map(item => <ChildA  key = {item} name={item}/>)}
                <button onClick={e => {
                    this.setState({
                        list: ['b','c','d']
                    })
                }}>set</button>
            </div>
        );
    }
}

class AppB extends Component{
    state = {
        num: 1
    }
    render() {
        return (
            <div>
                sss
            </div>
        );
    }
    componentDidUpdate(prevProps, prevState) {
        console.log(this.state.num)
    }
    componentDidMount() {
        let { num } = this.state
        this.setState({
            num: ++num
        })
        this.setState({
            num: ++num
        })
    }
}


export default AppB

