/**
 * Created by weiyun on 2019/10/24.
 */
import React,{Component} from 'react'


class Action extends Component{
    render(){
        return(
            <div>
                <input />
                <button>提交</button>
            </div>
        )
    }
}

class List extends Component{
    render(){
        return(
            <div>
                {this.props.data.map(item=><p key={item.id}>{item.name}</p>)}
            </div>
        )
    }
}

class App extends Component{

    constructor(props){
        super(props)
        this.state = {
            data:[
                {id:0,name:'a'},
                {id:1,name:'b'},
                {id:2,name:'c'},
                ]
        }
    }


    render(){
        return(
            <div>
                <Action/>
                <List data={this.state.data}/>
            </div>
        )
    }
}

export default App
