/**
 * Created by weiyun on 2019/10/24.
 */
import React,{Component} from 'react'


class Action extends Component{
    constructor(props){
        super(props)
        this.state ={
            value:''
        }
    }
    render(){
        let {
            onAdd
        }=this.props
        return(
            <div>
                <input value={this.state.value} onChange={(e)=>this.setState ({value:e.target.value})}/>
                <button onClick={()=>{
                        onAdd(this.state.value)
                        this.setState({value:''})
                    }
                }>提交</button>
            </div>
        )
    }
}

class List extends Component{
    render(){
        return(
            <div>
                {this.props.data.map(item=><p key={item.id}>{item.name}</p>)}
            </div>
        )
    }
}

class App extends Component{

    constructor(props){
        super(props)
        this.state = {
            data:[
                {id:0,name:'a'},
                {id:1,name:'b'},
                {id:2,name:'c'},
                ]
        }
    }


    render(){
        return(
            <div>
                <Action
                 onAdd ={(name)=>{
                     let data = this.state.data;
                     data.push({
                         name,
                         id:name
                     });
                     this.setState({
                         data:data
                     })
                 }}
                />
                <List data={this.state.data}/>
            </div>
        )
    }
}

export default App
