import React from 'react';
import ReactDOM from 'react-dom';
//import './demo0.js'
import './index.css';
import Confirm from './demo0.js';
import * as serviceWorker from './serviceWorker';

class App extends React.Component{
    constructor(props){
        super(props)
        this.state = {
            visible: false,
        }
    }
    openModal(){
        this.setState({
            visible: true
        })
    }
    onOk(){
        this.setState({
            visible:false
        })
    }
    onCancel(){
        this.setState({
            visible:false
        })
    }

    render() {

        return (
            <div className="App">
                <div onClick={this.openModal.bind(this)}>开启弹窗</div>
                <Confirm
                    width={'520px'}
                    height={'520px'}
                    visible={this.state.visible}
                    title={this.state.title}
                    content={this.state.content}
                    opacit={0.66}
                />
                <div onClick={this.onOk.bind(this)}>确定</div>
                <div onClick={this.onCancel.bind(this)}>取消</div>

            </div>

        );
    }


    async componentDidMount(){
        // let res = await confirm(this.props)
        // if(res) {
        //     console.log("是")
        // } else {
        //     console.log("否")
        // }
    }

}




ReactDOM.render(<App />, document.getElementById('root'));

