/**
 * Created by weiyun on 2019/9/12.
 */
import React,{Component} from 'react';
import ReactDOM from 'react-dom';
import './index.css';

var list = <div>list</div>
class List extends Component{
    render(){
        return (
            <div>
                list {this.props.name}
            </div>
        );
    }

    componentDidMount(){
        console.log('list did mount');
    }

    componentWillUpdate(){
        console.log('list will update');
    }
    componentWillUnmount(){
        console.log('list will unmount');
    }

}


class App extends Component{
    render(){
        return(
            <div>
                <List/>
                list
                <List/>
            </div>
        );
    }
}




class Child extends Component{
    render() {
        console.log('child render')
        return (
            <div>
                {this.props.name}
            </div>
        );
    }

    componentDidUpdate(prevProps, prevState) {
        console.log('child update')
    }

}

class Parent extends Component{
    state = {
        name: 'aaa'
    }
    render() {
        console.log('parent render')
        return (
            <div>
                <Child name={this.state.name}/>
                <button onClick={e => {

                    this.setState({name:'bbb'})
                }}>change</button>
            </div>
        );
    }
    componentDidUpdate(prevProps, prevState) {
        console.log('parent update')
    }
}


class ChildA extends Component{
    render() {
        return (
            <div>
                {this.props.name}
            </div>
        );
    }
    componentDidMount() {
        console.log('mount',this.props.name)
    }
    componentDidUpdate() {
        console.log('update',this.props.name)
    }
    componentWillUnmount() {
        console.log('unmount',this.props.name)
    }

}
class AppA extends Component{
    state = {
        list: ['a','b','c']
    }
    render() {
        return (
            <div>
                {this.state.list.map(item => <ChildA  key = {item} name={item}/>)}
                <button onClick={e => {
                    this.setState({
                        list: ['b','c','d']
                    })
                }}>set</button>
            </div>
        );
    }
}

class AppB extends Component{
    state = {
        num: 1
    }
    render() {
        return (
            <div>
                sss
            </div>
        );
    }
    componentDidUpdate(prevProps, prevState) {
        console.log(this.state.num)
    }
    componentDidMount() {
        let { num } = this.state
        this.setState({
            num: ++num
        })
        this.setState({
            num: ++num
        })
    }
}

class AppQ extends Component{
    state = {
        num: 1
    }
    render() {
        return (
            <div>
                sss
            </div>
        );
    }
    componentDidUpdate(prevProps, prevState) {
        console.log(this.state.num)
    }
    componentDidMount() {
        setTimeout(() => {
            console.log('timeout')
        })
        new Promise((resovle) => {
            console.log('before')
            resovle('then')
            console.log('after')
        }).then(res => {
            console.log(res)
        })
        let {num} = this.state
        this.setState({
            num: ++num
        }, () => {
            console.log('after setState')
        })
        console.log('---')
    }
}

function confirm(props){
    function onCancel(){
        console.log('onCancel');
    }
    function onOk(){
        console.log('onOk');
    }

    return(



        <div className="modal-box" style={{backgroundColor:`rgba(0, 0, 0, ${props.opacity})`}}>
            <div className="modal-content" style={{width:props.width,height:props.height}}>
            <div className={props.titleClass}>{props.title}</div>
                <div className={props.contentClass}>{props.content}</div>
                <div className={props.footerClass}>
                    <div onClick={onOk} className={props.okClass}>{props.okText}</div>
                    <div onClick={onCancel} className={props.cancelClass}>{props.cancelText}</div>
            </div>
            </div>
        </div>
    )
}

const noop = _=> undefined
confirm.defaultProps = {
    onOk: noop,
    onCancel: noop,
    okText: '确定',
    cancelText: '取消',
    titleClass: 'modal-title',
    contentClass: 'modal-text',
    footerClass: 'modal-footer',
    okClass: 'modal-confirm',
    cancelClass: 'modal-cancel',
    height:'auto',
    width:'520px',
    opacity: 0.6
}


export default confirm






